mapper.update:
UPDATE board
SET
    modifyDate = SYSDATE
  , boardTitle = #{boardTitle}
  , boardCnt = #{boardCnt}
  , boardWriter = #{boardWriter}
  , boardPwd = #{boardPwd}
WHERE
    boardId = #{boardId}
