mapper.insert:

    SELECT board_seq.nextval FROM dual

INSERT INTO board ()
VALUES (
    #{boardId}
    , #{boardTitle}
    , #{boardCnt}
    , #{boardWriter}
    , #{boardPwd}
    , 0
    , #{categoryId}
    , SYSDATE
    , null
    , 0)

mapper.update:
UPDATE board
SET
    modifyDate = SYSDATE
  , boardTitle = #{boardTitle}
  , boardCnt = #{boardCnt}
  , boardWriter = #{boardWriter}
  , boardPwd = #{boardPwd}
WHERE
    boardId = #{boardId}
