mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY boardId desc) as row_num,
FROM board WHERE boardDel = 0
) where row_num between #{pagination.limitStart} and #{pagination.limitEnd}

mapper.insert:

    SELECT board_seq.nextval FROM dual

INSERT INTO board ()
VALUES (
    #{boardId}
    , #{boardTitle}
    , #{boardCnt}
    , #{boardWriter}
    , #{boardPwd}
    , 0
    , #{categoryId}
    , SYSDATE
    , null
    , 0)

mapper.update:
UPDATE board
SET
    modifyDate = SYSDATE
  , boardTitle = #{boardTitle}
  , boardCnt = #{boardCnt}
  , boardWriter = #{boardWriter}
  , boardPwd = #{boardPwd}
WHERE
    boardId = #{boardId}
