mapper.insert:
INSERT INTO board ()
VALUES (
    #{boardTitle}
    , #{boardCnt}
    , #{boardWriter}
    , #{boardPwd}
    , #{groupId}
    , #{categoryId}
    , SYSDATE
    , null
    , 0)

mapper.update:
UPDATE board
SET
    modifyDate = SYSDATE
  , boardTitle = #{boardTitle}
  , boardCnt = #{boardCnt}
  , boardWriter = #{boardWriter}
WHERE
    boardId = #{boardId}
